import React, { useState } from "react";
import { Box, Button, Typography, TextField } from "@mui/material";
import axios from "axios";

const DownloadPage = () => {
  const [filename, setFilename] = useState("");
  const [message, setMessage] = useState("");

  const handleDownload = async () => {
    if (!filename) {
      setMessage("Please enter a filename.");
      return;
    }
    try {
      const response = await axios.get(
        `http://127.0.0.1:5000/download/${filename}`,
        {
          responseType: "blob", // For downloading files
        }
      );

      // Create a download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();

      setMessage("Download started.");
    } catch (error) {
      setMessage("File download failed. Ensure the filename is correct.");
    }
  };

  return (
    <Box
      textAlign="center"
      mt={5}
      sx={{
        backgroundColor: "#D3C5B6", // Primary background color for the whole page
        color: "#484237", // Text color
        padding: "20px",
        borderRadius: "10px",
        maxWidth: "500px",
        margin: "0 auto",
      }}
    >
      <Typography
        variant="h4"
        gutterBottom
        sx={{
          color: "#484237", // Text color
        }}
      >
        Download a File
      </Typography>
      <TextField
        label="Filename"
        variant="outlined"
        onChange={(e) => setFilename(e.target.value)}
        sx={{
          marginBottom: "20px",
          width: "300px",
          backgroundColor: "#B2A599", // Body color for the text field
          borderRadius: "5px",
          input: { color: "#484237" }, // Text color inside input
        }}
      />
      <br />
      <Button
        variant="contained"
        sx={{
          backgroundColor: "#B2A599", // Button color
          color: "#484237", // Button text color
          "&:hover": {
            backgroundColor: "#A39489", // Hover color for the button
          },
        }}
        onClick={handleDownload}
      >
        Download
      </Button>
      {message && (
        <Typography
          sx={{
            color: "#484237", // Message text color
            marginTop: "20px",
          }}
        >
          {message}
        </Typography>
      )}
    </Box>
  );
};

export default DownloadPage;
