import React, { useState } from "react";
import { Box, Button, Typography, Input } from "@mui/material";
import axios from "axios";

const FileUploadPage = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setMessage("Please select a file first.");
      return;
    }
    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://127.0.0.1:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage("File upload failed.");
    }
  };

  return (
    <Box
      textAlign="center"
      mt={5}
      sx={{
        backgroundColor: "#D3C5B6",  // Primary background color
        color: "#484237",            // Text color
        padding: "20px",             // Padding to add some spacing inside the box
        borderRadius: "10px",         // Rounded corners for a softer look
      }}
    >
      <Typography variant="h4" gutterBottom sx={{ mb: 2 }}>
        Upload a File
      </Typography>
      <Input
        type="file"
        onChange={handleFileChange}
        sx={{
          backgroundColor: "#B2A599",  // Body color
          color: "#484237",             // Text color for file input
          padding: "20px",              // Padding for the file input
          borderRadius: "10px",          // Rounded corners for the file input
          border: "1px solid #484237",  // Border to match text color
        }}
      />
      <Button
        variant="contained"
        color="primary"
        onClick={handleUpload}
        sx={{
          marginLeft: "10px",
          backgroundColor: "#484237",   // Button background color
          color: "#D3C5B6",              // Text color for button
          '&:hover': {
            backgroundColor: "#B2A599",  // Hover effect background color
          },
        }}
      >
        Upload
      </Button>
      {message && (
        <Typography color="textSecondary" mt={2}>
          {message}
        </Typography>
      )}
    </Box>
  );
};

export default FileUploadPage;
